#include <bits/stdc++.h>using namespace std;typedef long long ll;int main() {	// your code goes here    // int t;    // cin>>t;    // while(t--){        int n;        cin>>n;        vector<ll> a(n,0);        for(int i=0;i<n;i++)cin>>a[i];        map<ll,ll> mp;        ll sum=0;        for(int i=0;i<n;i++){            sum+=a[i];            mp[a[i]]++;        }        vector<ll> res;        for(int i=0;i<n;i++){            ll need=sum-a[i];            mp[a[i]]--;            if(need%2==0){                if(mp.find(need/2)!=mp.end()&&mp[need/2]>0){                    res.push_back(i+1);                }            }            mp[a[i]]++;        }        cout<<res.size()<<endl;        for(int i=0;i<res.size();i++){            cout<<res[i]<<" ";        }        cout<<endl;    // }}