#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    ll t;    cin>>t;    while(t--){        ll n;        cin>>n;        vector<pair<ll,ll>> ans;        vector<vector<int>> intervals(n);        for(int i=0;i<n;i++){            ll l,r;            cin>>l>>r;            ans.push_back({l,r});            intervals[i].push_back(l);            intervals[i].push_back(r);        }        sort(intervals.begin(),intervals.end());        vector<vector<int>> answer;        answer.push_back(intervals[0]);        for (int i=1;i<intervals.size();i++){            if (answer.back()[1]>=intervals[i][0]) answer.back()[1]=max(answer.back()[1],intervals[i][1]);            else answer.push_back(intervals[i]);        }        if(answer.size()<=1){            cout<<-1<<endl;        }        else{            for(int i=0;i<ans.size();i++){                if(ans[i].first>=answer[0][0]&&ans[i].second<=answer[0][1]){                    cout<<1<<" ";                }                else cout<<2<<" ";            }            cout<<endl;        }    }}