#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    // int t;    // cin >> t;    // while(t--){        ll n,m,k;        cin>>n>>m>>k;        vector<ll> a(n,0);        for(int i=0;i<n;i++) cin>>a[i];        priority_queue<ll> pq;        ll cost=a[n-1]-a[0]+1;        for(int i=0;i<n-1;i++){            pq.push(a[i+1]-a[i]);        }        while(k>1 && !pq.empty()){            cost-=pq.top()-1;            pq.pop();            k--;        }        cout<<cost<<endl;    // }    // return 0;}