#include <bits/stdc++.h>using namespace std;const int mod = 998244353;typedef long long ll;vector<ll> ans;ll del(ll root,vector<vector<ll>>& adj,vector<ll>& r){    ll res=r[root];    ll rc=0;    for(ll v:adj[root]){        rc+=del(v,adj,r);    }    if(res==1&&((rc>0&&rc==adj[root].size())||adj[root].size()==0))ans.push_back(root);    return res;}int main(){    ll n;    cin>>n;    vector<ll> p(n);    vector<ll> r(n);    for(int i=0;i<n;i++){        cin>>p[i]>>r[i];        if(p[i]>0)p[i]--;    }    vector<vector<ll>> adj(n);    ll root=-1;    for(int i=0;i<n;i++){        if(p[i]==-1){            root=i;        }        else adj[p[i]].push_back(i);    }    int cnt=del(root,adj,r);    if(ans.size()==0)cout<<-1<<endl;    else{        sort(ans.begin(),ans.end());        for(int i=0;i<ans.size();i++)cout<<ans[i]+1<<" ";        cout<<endl;    }}