#include <bits/stdc++.h>using namespace std;typedef long long ll;vector<int> parent;vector<int> sz;  int find(int u) {    if (parent[u] == u) return u;    return parent[u] = find(parent[u]); } void Union(int a, int b) {      int pa = find(a);    int pb = find(b);     if (pa != pb) {        if (sz[pa] > sz[pb]) {            parent[pb] = pa;            sz[pa] += sz[pb];        } else {            parent[pa] = pb;            sz[pb] += sz[pa];        }    }} int main(){    ll n,m;    cin>>n>>m;    parent.resize(n);    sz.assign(n,0);    for(int i=0;i<n;i++){        sz[i]=1;        parent[i]=i;    }    for(int i=0;i<m;i++){        ll x;        cin>>x;        ll u=-1;        for(int j=0;j<x;j++){            ll a;            cin>>a;            a--;            if(u==-1){                u=a;            }            else{                Union(u,a);            }        }    }    for(int i=0;i<n;i++){        cout<<sz[find(i)]<<" ";    }    cout<<endl;    return 0;}