#include <bits/stdc++.h>using namespace std;typedef long long ll; ll dynamic(int i, int j, vector<vector<ll>>& a, vector<vector<ll>>& dp){    if (i >= (int)a[0].size()) return 0;    if (dp[i][j] != -1) return dp[i][j];    ll take = a[j][i] + dynamic(i+1, 1-j, a, dp);    ll skip = dynamic(i+1, j, a, dp);    return dp[i][j] = max(take, skip);} int main(){    ll n;    cin >> n;    vector<vector<ll>> a(2, vector<ll>(n));    for(int i=0; i<n; i++) cin >> a[0][i];    for(int i=0; i<n; i++) cin >> a[1][i];     vector<vector<ll>> dp(n+1, vector<ll>(2, -1));     cout << max(dynamic(0, 0, a, dp), dynamic(0, 1, a, dp)) << endl;}