#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){        ll n;        cin>>n;        vector<ll> a(n);        ll sum=0;        ll mx=0;        for(int i=0;i<n;i++){            cin>>a[i];            sum+=a[i];            mx=max(mx,a[i]);        }        if(sum%2==1||mx>sum/2){            cout<<"NO"<<endl;        }        else{            cout<<"YES"<<endl;        }        return 0;}