#include <bits/stdc++.h>using namespace std;typedef long long ll;int main() {	// your code goes here    // int t;    // cin>>t;    // while(t--){        ll n;        cin>>n;        vector<ll> a(n,0);        for(int i=0;i<n;i++)cin>>a[i];        ll pre1=-1;        ll pre2=-1;        ll ans=0;        vector<ll> dp(n,0);        for(int i=0;i<n;i++){            if(a[i]>0){                if(pre1>=0){                    dp[i]=dp[pre1];                }            }            else{                if(pre2>=0){                    dp[i]=dp[pre2];                }                dp[i]+=(i-pre1);                pre2=pre1;                pre1=i;            }        }        for(int i=0;i<n;i++)ans+=dp[i];        cout<<ans<<" "<<((n*(n+1))/2-ans)<<endl;    // }}