#include <bits/stdc++.h>using namespace std;typedef long long ll;Â int main(){    ll n,m;    cin>>n>>m;    vector<ll> a(n);    for(int i=0;i<n;i++){        cin>>a[i];    }    if(n>m){        cout<<0<<endl;    }    else{        ll ans=1;        for(int i=0;i<n;i++){            for(int j=i+1;j<n;j++){                ans=(ans*abs(a[i]-a[j]))%m;                ans=ans%m;            }        }        cout<<ans<<endl;    }}