#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    ll t;    cin>>t;    while(t--){        ll n,k;        cin>>n>>k;        if(k>=n)cout<<1<<endl;        else{            ll ans=n;            for(int i=1;i<=min(k,(ll)sqrt(n));i++){                if(n%i==0&&n/i<=k){                    ans=i;                    break;                }                else{                    if(n%i==0)ans=n/i;                }            }            cout<<ans<<endl;        }    }}