#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    ll t;    cin>>t;    while(t--){        ll n,k;        cin>>n>>k;        map<ll,ll> mp;        for(int i=0;i<n;i++){            ll x;            cin>>x;            x%=k;            if(x>0)mp[k-x]++;        }        ll ans=0;        ll cycle=0;        ll val=0;        for(auto it:mp){            if(it.second>=cycle){                val=max(val,it.first);                cycle=it.second;            }        }        if(cycle>0)ans=(cycle-1)*k;        if(val>0)ans+=val+1;        cout<<ans<<endl;    }}