#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    int n,m;    cin>>n>>m;    vector<string> a(n);    for(int i=0;i<n;i++){        cin>>a[i];    }    vector<ll> cnt(m,0);    for(int i=0;i<m;i++){        unordered_set<char> s;        for(int j=0;j<n;j++){            s.insert(a[j][i]);        }        cnt[i]=s.size();    }    ll ans=1;    for(int i=0;i<m;i++){        ans=(ans*cnt[i])%1000000007;    }    cout<<ans<<endl;    return 0;}