#include <bits/stdc++.h>using namespace std;typedef long long ll;Â int main(){    ll t;    cin>>t;    while(t--){        ll n;        cin>>n;        vector<ll> a(n);        map<ll,ll> mp;        for(ll i=0;i<n;i++){            cin>>a[i];            mp[a[i]]=i+1;        }        sort(a.begin(),a.end());        int ans=0;        for(int i=0;i<n;i++){            for(int j=i+1;j<n;j++){                ll val=a[i]*a[j];                if(val>=2*n)break;                if(mp[a[i]]+mp[a[j]]==val){                    ans++;                }            }        }        cout << ans << endl;    }}