#include <bits/stdc++.h>using namespace std;typedef long long ll;ll mod=1000000007;int main(){    ll t;    cin>>t;    while(t--){        ll n;        char c;        string s;        cin>>n>>c;        cin>>s;        ll p1=0;        bool flag=false;        for(int i=0;i<n;i++){            if(s[i]==c){                p1=i+1;            }            else{                flag=true;            }        }        if(!flag){            cout<<0<<endl;        }        else if(p1>n/2){            cout<<1<<endl;            cout<<p1<<endl;        }        else{            cout<<2<<endl;            cout<<n<<" "<<n-1<<endl;        }    }}