#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<ll> a;        for(int i=0;i<n;i++){            ll x;            cin>>x;            if(a.size()==0)a.push_back(x);            else {                if(a.back()>0){                    if(x==0)a.push_back(x);                }                if(a.back()==0){                    if(x>0)a.push_back(x);                }            }        }        // cout<<"ans=";        ll nz=0;        for(int i=0;i<a.size();i++){            if(a[i]!=0)nz++;        }        if(nz==0)cout<<0<<endl;        else if(nz==1)cout<<1<<endl;        else cout<<2<<endl;    }}