#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    ll n;    cin>>n;    for(int i=0;i<n;i++){        int num1,num2;        cin>>num1>>num2;        int c2=0;        int c5=0;        int n1=num1;        while(num1>0&&num1%2==0){            num1/=2;            c2++;        }        while(num1>0&&num1%5==0){            num1/=5;            c5++;        }        ll ans=1;        if(c2<c5){            while(c2<c5&&ans*2<=num2){                ans*=2;                c2++;            }        }        else{            while(c5<c2&&ans*5<=num2){                ans*=5;                c5++;            }        }        while(ans*10<=num2){            ans*=10;        }        cout<<(ans*(num2/ans))        *1LL*n1<<endl;    }}