#include <bits/stdc++.h>using namespace std;typedef long long ll;int main() {	// your code goes here    int t;    cin>>t;    while(t--){        ll n;        cin>>n;        if(n%2==0){            cout<<n/2<<" "<<n/2<<endl;        }        else{            bool flag=false;            for(ll i=3;i<=sqrt(n);i+=2){                if(n%i==0){                    cout<<n/i<<" "<<n-(n/i)<<endl;                    flag=true;                    break;                }            }            if(!flag) cout<<1<<" "<<n-1<<endl;        }    }}