#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    ll n;    cin>>n;    string s;    cin>>s;    ll neg=0;    ll pos=0;    for(ll i=0;i<n;i++){        if(s[i]=='-')neg++;        else pos++;    }    ll t;    cin>>t;    for(ll i=0;i<t;i++){        ll c1,c2;        cin>>c1>>c2;        if(max(c1,c2)*min(neg,pos)>=min(c1,c2)*max(neg,pos)){            ll diff=abs(c1-c2);            ll need=max(c1,c2)*min(neg,pos) - min(c1,c2)*max(neg,pos);            if((diff==0||need%diff==0)&&need<=diff*max(neg,pos)){                cout<<"YES"<<endl;            }            else{                cout<<"NO"<<endl;            }        }        else{            cout<<"NO"<<endl;        }    }}