#include <bits/stdc++.h>using namespace std;typedef long long ll;ll up(ll val,ll st,ll offset,vector<ll>& a){    ll lo=st;    ll hi=a.size()-1;    while(lo<=hi){        ll mid=(lo+hi)/2;        if(a[mid]-offset<=val){            lo=mid+1;        }        else{            hi=mid-1;        }    }    return lo;}int main(){    int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<ll> a(n,0);        vector<ll> b(n,0);        for(int i=0;i<n;i++){            cin>>a[i];        }        for(int i=0;i<n;i++){            cin>>b[i];        }        vector<ll> cnt(n,0);        vector<ll> res(n,0);        vector<ll> saveb=b;        for(int i=1;i<n;i++){            b[i]+=b[i-1];        }        ll off=0;        for(int i=0;i<n;i++){            ll idx=up(a[i],i,off,b);            cnt[i]++;            if(idx<n){                cnt[idx]--;            }            if(idx<n){                if(idx>0)res[idx]+=(a[i]-b[idx-1]+off);                else res[idx]+=(a[i]+off);            }            off=b[i];        }        for(int i=1;i<n;i++){            cnt[i]+=cnt[i-1];        }        for(int i=0;i<n;i++){            res[i]+=cnt[i]*saveb[i];            cout<<res[i]<<" ";        }        cout<<endl;     } }