#include <bits/stdc++.h>using namespace std;typedef long long ll;int main() {	// your code goes here    int t;    cin>>t;    while(t--){        ll n;        cin>>n;        vector<ll> a(n);        vector<ll> e;        ll mx=0;        for(int i=0;i<n;i++){            cin>>a[i];            if(a[i]%2==0){                e.push_back(a[i]);            }            else {                mx=max(mx,a[i]);            }        }        if(e.size()==0||e.size()==n){            cout<<0<<endl;            continue;        }        else {            bool flag=false;            sort(e.begin(),e.end());            ll ans=0;            for(int i=0;i<e.size();i++){                if(mx>e[i]){                    ans++;                    mx+=e[i];                }                else{                    ans++;                    flag=true;                }            }            if(flag){                ans++;            }            cout<<ans<<endl;        }    }}