#include <bits/stdc++.h>using namespace std;typedef long long ll;int main() {	// your code goes here    int t;    cin>>t;    while(t--){        ll n,k;        cin>>n>>k;        vector<ll> a(n);        for(int i=0;i<n;i++){            cin>>a[i];        }        sort(a.begin(),a.end());        ll clc=2*k;        ll tp=k;        ll st=a[0];        for(int i=1;i<n;i++){            ll lft=a[i];            ll rht=a[i]+k;            ll step=(rht-st)/clc;            ll lft1=st+step*clc;            ll rht1=lft1+tp;            lft1=max(lft1,lft);            rht1=min(rht1,rht);            if(rht1<=lft1){                st=-1;                break;            }            st=lft1;            tp=rht1-lft1;        }        cout<<st<<endl;    }}