#include <bits/stdc++.h>using namespace std;typedef long long ll;int main() {	// your code goes here    int t;    cin>>t;    while(t--){        string str,t;        cin>>str>>t;        int i=0,j=0;        while(i<str.size() && j<t.size()){            if(str[i]==t[j]){                i++;                j++;            }            else{                if(str[i]=='?'){                    str[i]=t[j];                    i++;                    j++;                }                else{                    i++;                }            }        }        if(j<t.size()){            cout<<"NO"<<endl;            continue;        }        for(int i=0;i<str.size();i++){            if(str[i]=='?'){                str[i]='a';            }        }        cout<<"YES"<<endl;        cout<<str<<endl;    }}