#include<bits/stdc++.h>using namespace std;typedef long long ll;ll dynamic(ll i,ll prev,vector<ll>& a,vector<ll>& c,vector<vector<ll>>& dp){    if(i>=a.size())return 0;    if(dp[i][prev]!=-1)return dp[i][prev];    ll take=c[i]+dynamic(i+1,prev,a,c,dp);    ll skip=LLONG_MAX;    if(prev==0||a[i]>=a[prev]){        skip=dynamic(i+1,i,a,c,dp);    }    return dp[i][prev]=min(take,skip);}Â int main(){    cin.tie(0);    ios::sync_with_stdio(0);    ll t;    cin>>t;    while(t--){        ll n,m;        cin>>n>>m;        string str;        cin>>str;        set<ll> s;        for(int i=0;i<m;i++){            ll x;            cin>>x;            s.insert(x);        }        ll st=1;        for(int i=0;i<str.size();i++){            st++;            if(str[i]=='B'){                while(s.find(st)!=s.end())st++;                s.insert(st);                while(s.find(st)!=s.end())st++;            }            else s.insert(st);        }        cout<<s.size()<<endl;        for(auto x:s){            cout<<x<<" ";        }        cout<<endl;    }}