#include<bits/stdc++.h>using namespace std;typedef long long ll;int main(){    ios::sync_with_stdio(0);    cin.tie(0);    ll t;    cin>>t;    while(t--){        ll n,m;        cin>>n>>m;        vector<ll> a(n);        vector<ll> b(m);        vector<ll> c(m);        multiset<ll> st;        for(ll i=0;i<n;i++){            cin>>a[i];            st.insert(a[i]);        }        for(int i=0;i<m;i++){            cin>>b[i];        }        for(int i=0;i<m;i++){            cin>>c[i];        }        vector<pair<ll,ll>> v;        for(int i=0;i<m;i++){            v.push_back({b[i],-c[i]});        }        sort(v.begin(),v.end());        ll ans=0;        for(int i=0;i<m;i++){            if(v[i].second==0)continue;            auto it=st.lower_bound(v[i].first);            if(it!=st.end()){                ll x=max(*it,-v[i].second);                ans++;                st.erase(it);                st.insert(x);            }        }        for(int i=0;i<m;i++){            if(v[i].second==0){                auto it=st.lower_bound(v[i].first);                if(it!=st.end()){                    ans++;                    st.erase(it);                }            }        }        cout<<ans<<endl;    }}