#include<bits/stdc++.h>using namespace std;typedef long long ll;ll dynamic(ll i,ll prev,vector<ll>& a,vector<ll>& c,vector<vector<ll>>& dp){    if(i>=a.size())return 0;    if(dp[i][prev]!=-1)return dp[i][prev];    ll take=c[i]+dynamic(i+1,prev,a,c,dp);    ll skip=LLONG_MAX;    if(prev==0||a[i]>=a[prev]){        skip=dynamic(i+1,i,a,c,dp);    }    return dp[i][prev]=min(take,skip);}Â int main(){    cin.tie(0);    ios::sync_with_stdio(0);    ll t;    cin>>t;    while(t--){        ll n;        cin>>n;        vector<ll> a(n+1);        vector<ll> c(n+1);        for(ll i=0;i<n;i++){            cin>>a[i+1];            }        for(ll i=0;i<n;i++){            cin>>c[i+1];        }        vector<ll> next(n+1, 0);         vector<ll> cur(n+1);        for(ll i=n; i>=1; i--){            for(ll prev=0; prev<=n; prev++){                ll take = (next[prev]==LLONG_MAX ? LLONG_MAX : c[i]+next[prev]);                ll skip = LLONG_MAX;                if(prev==0 || a[i]>=a[prev]) skip = next[i];                cur[prev] = min(take, skip);            }            next.swap(cur);        }        cout<<next[0]<<endl;    }}