#include<bits/stdc++.h>using namespace std;typedef long long ll;ll left(ll x,ll y,ll num){    while(x--){        num-=(num/y);    }    return num;}int main(){    ll t;    cin>>t;    while(t--){        ll x,y,k;        cin>>x>>y>>k;        if(left(x,y,1e12)<k)cout<<-1<<endl;        else{            ll lo=1;            ll hi=1e12;            ll ans=-1;            while(lo<=hi){                ll mid=(lo+hi)/2;                if(left(x,y,mid-1)>k-1){                    hi=mid-1;                }                else{                    if(left(x,y,mid-1)==k-1&&left(x,y,mid)==k){                        ans=mid;                    }                    lo=mid+1;                }            }            cout<<ans<<endl;        }    }}