#include<bits/stdc++.h>using namespace std;typedef long long ll;int main(){    ll t;    cin>>t;    while(t--){        ll n,k;        cin>>n>>k;        map<ll,ll> mp1;        for(int i=0;i<n;i++){            ll x;            cin>>x;            mp1[x]++;        }        vector<ll> a(n);        for(int i=0;i<n;i++){            cin>>a[i];        }        sort(a.begin(),a.end());        ll cnt=0;        for(int i=0;i<n;i++){            ll y=a[i]+1;            ll num=(k-a[i])/y;            auto it=mp1.lower_bound(num+1);            if(it==mp1.begin()) continue;            it--;            it->second--;            if(it->second==0){                mp1.erase(it->first);            }            cnt++;        }        cout<<cnt<<endl;    }}