#include<bits/stdc++.h>using namespace std;typedef long long ll;int main(){    ll t;    cin>>t;    while(t--){        ll n;        cin>>n;        vector<ll> a(n);        vector<ll> b(n);        ll xa=0;        ll xb=0;        for(int i=0;i<n;i++){            cin>>a[i];            xa^=a[i];        }        for(int i=0;i<n;i++){            cin>>b[i];            xb^=b[i];        }        for(int i=0;i<n;i++){            if(i%2==0){                ll bitm=(a[i]^b[i]);                if(((xa^bitm)-(xb^bitm))>(xa-xb)){                    xa=xa^bitm;                    xb=xb^bitm;                }            }            else{                ll bitm=(a[i]^b[i]);                if(((xa^bitm)-(xb^bitm))<(xa-xb)){                    xa=xa^bitm;                    xb=xb^bitm;                }            }        }        if(xa==xb){            cout<<"Tie"<<endl;        }        else if(xa>xb){            cout<<"Ajisai"<<endl;        }        else{            cout<<"Mai"<<endl;        }    }}