#include<bits/stdc++.h>using namespace std;typedef long long ll;int main(){    ll t;    cin>>t;    while(t--){        ll n;        cin>>n;        vector<ll> a(n);        for(int i=0;i<n;i++){            cin>>a[i];        }        vector<ll> mx=a;        vector<ll> mn=a;        for(int i=1;i<n;i++){            mn[i]=min(mn[i],mn[i-1]);        }        for(int i=n-2;i>=0;i--){            mx[i]=max(mx[i],mx[i+1]);        }        bool f=false;        for(int i=0;i<n-1;i++){            if(mn[i]>mx[i+1]){                f=true;                break;            }        }        if(!f){            cout<<"YES"<<endl;        }        else{            cout<<"NO"<<endl;        }    }}