#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    int t;    cin >> t;    while(t--){        ll n,k;        cin>>n>>k;        string s;        cin>>s;        ll ans=0;        for(int i=0;i<n;i++){            if(s[i]=='1'){                for(int j=i+1;j<min(n,i+k+1);j++){                    if(s[j]=='0'){                        s[j]='.';                    }                }            }        }        for(int i=0;i<n;i++){            if(s[i]=='0')ans++;        }        cout<<ans<<endl;    }}