#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    int t;    cin >> t;    while(t--){        ll n;        cin>>n;        vector<vector<ll>> arr(2,vector<ll>(n,0));        for(int i=0;i<n;i++){            cin>>arr[0][i];        }        for(int i=0;i<n;i++){            cin>>arr[1][i];        }        ll num1=max(-arr[0][0],arr[1][0]);        ll num2=min(-arr[0][0],arr[1][0]);        for(int i=1;i<n;i++){            ll new1=max({arr[1][i]-num1, arr[1][i]-num2, num1-arr[0][i], num2-arr[0][i]});            ll new2=min({arr[1][i]-num2, arr[1][i]-num1, num1-arr[0][i], num2-arr[0][i]});            num1=new1;            num2=new2;        }        cout<<max(num1,num2)<<endl;    }}