#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    int t;    cin >> t;    while(t--){        ll n;        cin>>n;        set<ll> st;        for(int i=0;i<n;i++){            ll x;            cin>>x;            st.insert(x);        }        ll len=st.size();        ll ans=*st.lower_bound(len);        cout<<ans<<endl;    }}