#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    ll p;    cin>>p;    while(p--){        string s,t;        cin>>s>>t;        vector<int> mps(26,0);        vector<int> mpt(26,0);        vector<char> v={'a','b','c','d','e','f','g','h','i','j','k','l','m','n','o','p','q','r','s','t','u','v','w','x','y','z'};        for(int i=0;i<s.size();i++)mps[s[i]-'a']++;        for(int i=0;i<t.size();i++)mpt[t[i]-'a']++;        bool f=true;        for(int i=0;i<mps.size();i++){            if(mps[i]>mpt[i]){                f=false;                break;            }        }        if(!f)cout<<"Impossible"<<endl;        else{            string res="";            for(int i=0;i<s.size();i++){                for(int j=0;j<s[i]-'a';j++){                    while(mpt[j]>mps[j]){                        res+=v[j];                        mpt[j]--;                    }                }                res+=s[i];                mpt[s[i]-'a']--;                mps[s[i]-'a']--;            }            for(int i=0;i<26;i++){                while(mpt[i]>0){                    res+=v[i];                    mpt[i]--;                }            }            cout<<res<<endl;        }    }}