#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    ll t;    cin>>t;    while(t--){        ll n;        cin>>n;        vector<ll> a(n);        for(int i=0;i<n;i++){            cin>>a[i];        }        for(int i=0;i<n;i++){            if(a[i]==-1)continue;            for(int j=i+1;j<n;j++){                if(a[j]<a[i])a[j]=-1;            }        }        ll ans=0;        for(int i=0;i<n;i++){            if(a[i]==-1)ans++;        }        cout<<ans<<endl;    }}