#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    ll t;    cin>>t;    while(t--){        ll n;        cin>>n;        string s;        cin>>s;        int mx=0;        int cnt=0;        int j=0;        int st=0;        int end=n-1;        while(j<n&&s[j]=='0'){            cnt++;            j++;        }        st=j;        j=n-1;        while(j>=0&&s[j]=='0'){            cnt++;            j--;        }        end=j;        mx=max(mx,cnt);        cnt=0;        for(int i=st;i<=end;i++){            if(s[i]=='0'){                cnt++;                mx=max(mx,cnt);            }            else{                cnt=0;            }        }        cout<<mx<<endl;    }}