#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    ll t;    cin>>t;    while(t--){        ll n;        cin>>n;        vector<ll> a(n);        for(ll i=0;i<n;i++){            cin>>a[i];        }        vector<ll> o;        vector<ll> e;        for(ll i=0;i<n;i++){            if(a[i]%2==1) o.push_back(a[i]);            else e.push_back(a[i]);        }        sort(o.begin(),o.end(),greater<ll>());        sort(e.begin(),e.end(),greater<ll>());        ll os=o.size();        ll es=e.size();        vector<ll> po(os+1,0);        vector<ll> pe(es+1,0);        for(ll i=0;i<os;i++){            po[i+1]=po[i]+o[i];        }        for(ll i=0;i<es;i++){            pe[i+1]=pe[i]+e[i];        }        for(ll k=1;k<=n;k++){            ll ans=0;            if(os==0){                cout<<0;                if(k<n) cout<<" ";                continue;            }            ll maxf=es;            if(maxf>k-1) maxf=k-1;            for(ll f=maxf;f>=0;f--){                ll s=k-1-f;                if(s<0) continue;                ll maxx=s;                if(maxx>os-1) maxx=os-1;                ll minx=s-(es-f);                if(minx<0) minx=0;                if(minx>maxx) continue;                ll x=minx;                if(x%2==1) x++;                if(x<=maxx){                    ll now=o[0]+pe[f];                    if(now>ans) ans=now;                    break;                }            }            cout<<ans;            if(k<n) cout<<" ";        }        cout<<endl;    }    return 0;}