#include<bits/stdc++.h>using namespace std;typedef long long ll;int main(){    ll t;    cin>>t;    while(t--){        string s;        cin>>s;        ll cnt=0;        for(int i=1;i<s.size()-1;i++){            if(s[i]=='u'&&s[i+1]=='u'){                cnt++;                s[i+1]='s';            }        }        if(s[0]=='u')cnt++;        if(s[s.size()-1]=='u')cnt++;        cout<<cnt<<endl;    }}