#include<bits/stdc++.h>using namespace std;typedef long long ll;int main(){    ll t;    cin>>t;    while(t--){        ll n;        cin >> n;        vector<ll> a(n);        for(int i = 0; i < n; i++){            cin >> a[i];        }        vector<ll> suf(n+1, 0);        for(int i = n-1; i >= 0; i--){            suf[i] = suf[i+1] + a[i];        }        vector<ll> pre(n, 0);        for(int i = 1; i < n; i++){            pre[i] = pre[i-1] + abs(a[i]);        }        ll ans = -suf[1];        for(int i = 1; i < n; i++){            ll left = a[0] + pre[i-1];            ll right = 0;            if(i+1 <= n-1){                right = suf[i+1];            }            ans = max(ans, left - right);        }        cout << ans << endl;    }    return 0;}