#include <bits/stdc++.h>using namespace std;typedef long long ll;ll mod=1000000007;ll gcd(ll a,ll b){    if(b==0){        return a;    }    return gcd(b,a%b);}int main(){    ll t;    cin>>t;    while(t--){        ll l,a,b;        cin>>l>>a>>b;        b=b%l;        if(b==0){            cout<<a<<endl;            continue;        }        ll gc=gcd(l,b);        ll ans=a+gc*((l-1-a)/gc);        cout<<ans<<endl;    }}