#include<bits/stdc++.h>using namespace std;typedef long long ll;int main(){    ll t;    cin>>t;    while(t--){      ll n;      cin>>n;      vector<string> a(n);      string s="";      for(int i=0;i<n;i++){        cin>>a[i];      }      for(int i=0;i<n;i++){            if(a[i]+s<=s+a[i]){                s=a[i]+s;            }            else{                s=s+a[i];            }        }      cout<<s<<endl;    }}