#include<bits/stdc++.h>using namespace std;typedef long long ll;int main(){    ll t;    cin>>t;    while(t--){        ll n,k;        cin>>n>>k;        vector<ll> a(n);        map<ll,ll> mp;        for(ll i=0;i<n;i++){            cin>>a[i];            mp[a[i]]++;        }        ll turn=n-k+1;        for(auto it=mp.begin();it!=mp.end();it++){            if(turn<=0)break;            turn-=it->second-1;        }        while(turn>0&& !mp.empty()){            auto it=prev(mp.end());            mp.erase(it);            turn--;        }        ll ans=0;        for(auto it=mp.begin();it!=mp.end();it++){            if(it->first==ans)ans++;            else break;        }        cout<<ans<<endl;    }}