#include<bits/stdc++.h>using namespace std;typedef long long ll;int main(){    cin.tie(0);    ios::sync_with_stdio(0);    ll t;    cin>>t;    while(t--){        ll s,k,m;        cin>>s>>k>>m;        if(k>=s){            if(m%k<=s){                cout<<s-m%k<<endl;            }            else cout<<0<<endl;        }        else{            ll rem=m%k;            ll ext=s-k;            ll div=m/k;            if(div%2==0){                cout<<s-rem<<endl;            }            else cout<<k-rem<<endl;        }    }}