#include<bits/stdc++.h>using namespace std;typedef long long ll;int main(){    ios::sync_with_stdio(0);    cin.tie(0);    ll t;    cin>>t;    while(t--){        ll n,k;        cin>>n>>k;        if(n==k){            cout<<0<<endl;            continue;        }        queue<ll> q;        unordered_map<ll,ll> d;        q.push(n);        d[n]=0;        bool f=false;        while(!q.empty()){            ll v=q.front();            q.pop();            ll cur=d[v];            ll a=v/2;            ll b=(v+1)/2;            if(a>=k&&d.find(a)==d.end()){                d[a]=cur+1;                if(a==k){                    cout<<cur+1<<endl;                    f=true;                    break;                }                q.push(a);            }            if(b>=k&&d.find(b)==d.end()){                d[b]=cur+1;                if(b==k){                    cout<<cur+1<<endl;                    f=true;                    break;                }                q.push(b);            }        }        if(!f)cout<<-1<<endl;    }}