#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<int> a(n);        for(int i=0;i<n;i++){            cin>>a[i];        }        int ans=0;        for(int i=0;i<n;i++){            for(int j=i;j<n;j++){                vector<int> v=a;                swap(v[i],v[j]);                int mx=0;                int sum=0;                for(int k=0;k<n;k++){                    mx=max(mx,v[k]);                    sum+=mx;                }                ans=max(ans,sum);            }        }        cout<<ans<<endl;    }}