#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<int> a(n);        for(int i=0;i<n;i++){            cin>>a[i];        }        sort(a.begin(),a.end());        int cnt=1;        int ans=1;        int pre=a[0];        for(int i=1;i<n;i++){            if(a[i]-pre==1){                cnt++;                ans=max(cnt,ans);            }            else if(a[i]-pre>0){                cnt=1;            }            pre=a[i];        }        cout<<ans<<endl;    }}