#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    int t;    cin>>t;    while(t--){        int n,m,h;        cin>>n>>m>>h;        vector<int> a(n);        vector<int> last(n,-1);        for(int i=0;i<n;i++){            cin>>a[i];        }        vector<int> mp=a;        int rst=0;        int tim=1;        for(int i=0;i<m;i++){            int b,c;            cin>>b>>c;            b--;            int cur=0;            if(last[b]<rst)cur=a[b];            else cur=mp[b];            cur+=c;            if(cur>h){                rst=tim++;               }             else {                mp[b]=cur;                last[b]=tim++;            }        }        for(int i=0;i<n;i++){            if(last[i]<rst)cout<<a[i]<<" ";            else cout<<mp[i]<<" ";        }        cout<<endl;    }}