#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    ll t;    cin>>t;    while(t--){        ll n;        cin>>n;        if(n%2==0){            ll num=n/2;            while(num>0){                cout<<num<<" "<<n-num+1<<" ";                num--;            }            cout<<endl;        }        else{            ll num=(n+1)/2;            cout<<num<<" ";            num--;            while(num>0){                cout<<num<<" "<<n-num+1<<" ";                num--;                }            cout<<endl;        }    }}