#include<bits/stdc++.h>using namespace std;typedef long long ll;int main(){    int t;    cin>>t;    while(t--){        int n;        cin>>n;        string s;        cin>>s;        int ct=0;        for(char c:s)if(c=='1')ct++;        if(ct==0)cout<<(n+2)/3<<endl;        else{            int ans=0,cnt=0;            bool f=0;            for(int i=0;i<n;i++){                if(s[i]=='0')cnt++;                else{                    if(!f){                        ans+=(cnt+1)/3;                        f=1;                    }                    else ans+=cnt/3;                    cnt=0;                }            }            ans+=(cnt+1)/3;            cout<<ct+ans<<endl;        }    }    return 0;}