#include <bits/stdc++.h>using namespace std;typedef long long ll;Â int main(){    int t;    cin >> t;    while(t--){        ll n;        cin >> n;        vector<ll> a(n);        for(int i=0;i<n;i++) cin>>a[i];        vector<ll> b=a;        sort(b.begin(),b.end());        if(a==b){            cout << -1 << endl;            continue;        }        ll mn=b[0];        ll mx=b[n-1];        ll k=1e10;        for(int i=0;i<n;i++){            if(a[i]!=b[i]){                k=min(k, max(abs(a[i]-mn), abs(a[i]-mx)));            }        }        cout << k << endl;    }    return 0;}