#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    int t;    cin>>t;    while(t--){        int n,h,l;        cin>>n>>h>>l;        vector<int> a(n);        for(int i=0;i<n;i++) cin>>a[i];        int x=0;        int y=0;        for(int i=0;i<n;i++){            if(a[i]<=h)x++;            if(a[i]<=l)y++;        }        int both=min(x,y);        int ext=max(x,y)-both;        int ans=0;        int add=min(both,ext);        both-=add;        ans+=add;        both=max(0,both);        ans+=both/2;        cout<<ans<<endl;    }}